const id= 0;   //户号id,多户号修改: 0  1  2  3
const powerUserList = msg.payload.data.powerUserList[id]; //数据部分json

/* 重写日用电结构  */
//提取文件json日用电数据（大概是最近40天左右）
const daily_bill_list = powerUserList.daily_bill_list;
//重写结构
const daylist1 = daily_bill_list.map(item => ({
    day: item.day.substring(0, 4) + "-" + item.day.substring(4, 6) + "-" + item.day.substring(6),
    dayEleNum: Number(item.dayElePq),
    dayTPq: Number(item.thisTPq),
    dayPPq: Number(item.thisPPq),
    dayNPq: Number(item.thisNPq),
    dayVPq: Number(item.thisVPq),
}));
//提取所有的日用电，在每个月用电下面，需要for循环提取出来
const daylist2 = [];
for (var i = 0; i < powerUserList.month_bill_list.length; i++) {
    daylist2.push(powerUserList.month_bill_list[i].daily_ele); // 逐个添加
}
const daylist3 = daylist2.flat();  //将数据展平
//重写结构
const daylist4 = daylist3.map(item => ({
    day: item.day.substring(0, 4) + "-" + item.day.substring(4, 6) + "-" + item.day.substring(6),
    dayEleNum: Number(item.dayElePq),
    dayTPq: Number(item.thisTPq),
    dayPPq: Number(item.thisPPq),
    dayNPq: Number(item.thisNPq),
    dayVPq: Number(item.thisVPq),
}));
//合并daylist4和daylist1
daylist1.push(...daylist4)
//筛选重复，只保留数值大的数
const daylist5 = {};
daylist1.forEach(item => {
    const { day, dayEleNum } = item;
    if (!daylist5[day] || dayEleNum > daylist5[day].dayEleNum) {
        daylist5[day] = { ...item };
    }
});
//按日期重新排序
const daylist6 = Object.values(daylist5).sort((a, b) => a.day.localeCompare(b.day));
//取最新的370个数据（总数大概370-390波动）
const dayList7 = daylist6.reverse().slice(0, 370);


/*  增加每日电费金额
*   请根据自己的情况计算每日电费金额
*/
const tierConfig = {
    tier1: {
        limit: 2160,
        prices: {
            dayTPq: 0.00,   // 尖时段
            dayPPq: 0.59,   // 峰时段
            dayNPq: 0.00,   // 平时段
            dayVPq: 0.44    // 谷时段
        }
    },
    tier2: {
        limit: 3120,
        prices: {
            dayTPq: 0.00,   // 尖时段
            dayPPq: 0.64,   // 峰时段
            dayNPq: 0.00, // 平时段
            dayVPq: 0.49    // 谷时段
        }
    },
    tier3: {
        prices: {
            dayTPq: 0.00,   // 尖时段
            dayPPq: 0.89,   // 峰时段
            dayNPq: 0.00,   // 平时段
            dayVPq: 0.74    // 谷时段
        }
    }
};
// 时段优先级顺序
const timeSlotOrder = ['dayTPq', 'dayPPq', 'dayNPq', 'dayVPq'];
const dayList = dayList7.map(item => {
    const date = new Date(item.day);
    const year = date.getFullYear();
    const cyclestartDate = new Date(year, 0, 1); // 1月1日
    // 计算累计用电量
    const cycleDays = dayList7.filter(d => {
        const dDate = new Date(d.day);
        return dDate >= cyclestartDate && dDate <= date;
    });
    const cumulativeUsage = cycleDays.reduce((sum, d) => sum + d.dayEleNum, 0);
    // 计算当日电费
    let dayEleCost = 0;
    const usage = item.dayEleNum;
    let remainingUsage = usage;
    const usageBeforeToday = cumulativeUsage - usage;
    // 第一阶梯计算
    if (usageBeforeToday < tierConfig.tier1.limit) {
        const tier1Remaining = tierConfig.tier1.limit - usageBeforeToday;
        const tier1Usage = Math.min(remainingUsage, tier1Remaining);
        // 按时段优先级计算
        for (const slot of timeSlotOrder) {
            if (tier1Usage <= 0) break;

            const slotUsage = Math.min(item[slot], tier1Usage);
            dayEleCost += slotUsage * tierConfig.tier1.prices[slot];
            remainingUsage -= slotUsage;
        }
    }
    // 第二阶梯计算
    if (remainingUsage > 0 && usageBeforeToday < tierConfig.tier2.limit) {
        const tier2Start = tierConfig.tier1.limit;
        const tier2Remaining = tierConfig.tier2.limit - Math.max(usageBeforeToday, tier2Start);
        const tier2Usage = Math.min(remainingUsage, tier2Remaining);

        // 按时段优先级计算
        for (const slot of timeSlotOrder) {
            if (tier2Usage <= 0) break;

            const slotUsage = Math.min(item[slot], tier2Usage);
            dayEleCost += slotUsage * tierConfig.tier2.prices[slot];
            remainingUsage -= slotUsage;
        }
    }
    // 第三阶梯计算
    if (remainingUsage > 0) {
        // 按时段优先级计算
        for (const slot of timeSlotOrder) {
            if (remainingUsage <= 0) break;

            const slotUsage = Math.min(item[slot], remainingUsage);
            dayEleCost += slotUsage * tierConfig.tier3.prices[slot];
            remainingUsage -= slotUsage;
        }
    }
    return {
        day: item.day,
        dayEleNum: item.dayEleNum,
        dayEleCost: parseFloat(dayEleCost.toFixed(2)), // 保留两位小数
        dayTPq: item.dayTPq,
        dayPPq: item.dayPPq,
        dayNPq: item.dayNPq,
        dayVPq: item.dayVPq,
    };
});

//重写月用电结构
const month_bill_list = powerUserList.month_bill_list;
const monthList = month_bill_list.map(item => ({
    month: item.month.substring(0, 4) + "-" + item.month.substring(4), //日期
    monthEleNum: Number(item.monthEleNum),       //总
    monthEleCost: Number(item.monthEleCost),     //金额
    monthTPq: Number(item.month_t_ele_num),      //尖
    monthPPq: Number(item.month_p_ele_num),      //峰
    monthNPq: Number(item.month_n_ele_num),      //平
    monthVPq: Number(item.month_v_ele_num),      //谷
}));

//增加上月，统计日用电
const prevMonth = new Date(new Date().setMonth(new Date().getMonth() - 1)).toISOString().slice(0, 7);
const premonthListExists = monthList.some(item => item.month === prevMonth);
if (!premonthListExists) {
    const yearMonth = prevMonth.replace('-', '');
    const daysInCurrentMonth = dayList.filter(dayItem => {
        const dayYearMonth = dayItem.day.slice(0, 7).replace('-', '');
        return dayYearMonth === yearMonth;
    });
    let monthEleNum = 0;
    let monthTPq = 0;
    let monthPPq = 0;
    let monthNPq = 0;
    let monthVPq = 0;
    let monthEleCost = 0; 
    daysInCurrentMonth.forEach(day => {
        monthEleNum += parseFloat(day.dayEleNum) || 0;
        monthTPq += parseFloat(day.dayTPq) || 0;
        monthPPq += parseFloat(day.dayPPq) || 0;
        monthNPq += parseFloat(day.dayNPq) || 0;
        monthVPq += parseFloat(day.dayVPq) || 0;
        monthEleCost += parseFloat(day.dayEleCost) || 0;
    });

    monthList.unshift({
        month: prevMonth,
        monthEleNum: Number(monthEleNum.toFixed(2)),
        monthEleCost: Number(monthEleCost.toFixed(2)),
        monthTPq: Number(monthTPq.toFixed(2)),
        monthPPq: Number(monthPPq.toFixed(2)),
        monthNPq: Number(monthNPq.toFixed(2)),
        monthVPq: Number(monthVPq.toFixed(2))
    });
}

//增加当月，统计日用电
const currentMonth = new Date().toISOString().slice(0, 7);
const monthListExists = monthList.some(item => item.month === currentMonth);
if (!monthListExists) {
    const yearMonth = currentMonth.replace('-', '');
    const daysInCurrentMonth = dayList.filter(dayItem => {
        const dayYearMonth = dayItem.day.slice(0, 7).replace('-', '');
        return dayYearMonth === yearMonth;
    });
    let monthEleNum = 0;
    let monthTPq = 0;
    let monthPPq = 0;
    let monthNPq = 0;
    let monthVPq = 0;
    let monthEleCost = 0; 
    daysInCurrentMonth.forEach(day => {
        monthEleNum += parseFloat(day.dayEleNum) || 0;
        monthTPq += parseFloat(day.dayTPq) || 0;
        monthPPq += parseFloat(day.dayPPq) || 0;
        monthNPq += parseFloat(day.dayNPq) || 0;
        monthVPq += parseFloat(day.dayVPq) || 0;
        monthEleCost += parseFloat(day.dayEleCost) || 0;
    });

    monthList.unshift({
        month: currentMonth,
        monthEleNum: Number(monthEleNum.toFixed(2)),
        monthEleCost: Number(monthEleCost.toFixed(2)),
        monthTPq: Number(monthTPq.toFixed(2)),
        monthPPq: Number(monthPPq.toFixed(2)),
        monthNPq: Number(monthNPq.toFixed(2)),
        monthVPq: Number(monthVPq.toFixed(2))
    });
}

//重写年用电结构
const yearMap = {}; // 用于按年份暂存数据
monthList.forEach(monthData => {
    const year = monthData.month.substring(0, 4);
    if (!yearMap[year]) {
        yearMap[year] = {
            year: year,
            yearEleNum: 0,
            yearEleCost: 0,
            yearTPq: 0,
            yearPPq: 0,
            yearNPq: 0,
            yearVPq: 0
        };
    }
    yearMap[year].yearEleNum += parseFloat(monthData.monthEleNum) || 0;
    yearMap[year].yearEleCost += parseFloat(monthData.monthEleCost) || 0;
    yearMap[year].yearTPq += parseFloat(monthData.monthTPq) || 0;
    yearMap[year].yearPPq += parseFloat(monthData.monthPPq) || 0;
    yearMap[year].yearNPq += parseFloat(monthData.monthNPq) || 0;
    yearMap[year].yearVPq += parseFloat(monthData.monthVPq) || 0;
});
const yearList = Object.values(yearMap).map(yearData => ({
    ...yearData,
    yearEleNum: Number(yearData.yearEleNum.toFixed(2)),
    yearEleCost: Number(yearData.yearEleCost.toFixed(2)),
    yearTPq: Number(yearData.yearTPq.toFixed(2)),
    yearPPq: Number(yearData.yearPPq.toFixed(2)),
    yearNPq: Number(yearData.yearNPq.toFixed(2)),
    yearVPq: Number(yearData.yearVPq.toFixed(2))
}));


/*输出json*/
msg.date = powerUserList.refresh_time;
msg.balance = powerUserList.balance;
msg.dayList = dayList;
msg.monthList = monthList;
msg.yearList = yearList.reverse();
return msg;
